mapper.delete:
  	DELETE FROM articale
  	WHERE id in 
  	
		#{id}

mapper.select:
SELECT a.id id,a.title title,a.face_cover faceCover,a.praise praise,a.comment_sum commentSum,a.browse_sum browseSum, a.create_time create_time,a.update_time update_time,a.approval approval,a.oppose oppose, a.status status,a.user_id user_id,u.username user_name FROM articale a LEFT JOIN articale_category ac on a.id = ac.articale_id LEFT JOIN category c on c.id = ac.category_id LEFT JOIN `user` u on u.id = a.user_id
and (a.title like '%${keyword}%' or c.name like '%${keyword}%' or a.content like '%${keyword}%')
GROUP BY a.id ORDER BY a.create_time desc LIMIT #{skip}, #{pageSize}

SELECT count(1)
FROM articale a 
WHERE id = #{id}
